body.script:
var loadDeferredStyles = function () {
    var addStylesNode = document.getElementById("deferred-styles");
    var replacement = document.getElementById("render-styles");
    replacement.innerHTML = addStylesNode.textContent;
    document.body.appendChild(replacement)
    addStylesNode.parentElement.removeChild(addStylesNode);
};
var raf = requestAnimationFrame || mozRequestAnimationFrame ||
    webkitRequestAnimationFrame || msRequestAnimationFrame;
if (raf) raf(function () { window.setTimeout(loadDeferredStyles, 0); });
else window.addEventListener('load', loadDeferredStyles);

$(document).ready(function () {
    //Hover Mở tài khoản ngay
    function enableHoverPopout() {
        //Move in
        $(".fixedfooter").css("border", "2px solid #F39200");
        $(".fixedfooter_img").css('background', 'url("../../../../../Content/theme/default/images/newmenu/popout_menu1_change.png") no-repeat center');
        $(".fixedfooter_go").css('background', 'url("../../../../../Content/theme/default/images/newmenu/popout_go_change.png") no-repeat center');
        $(".textPopout").css('color', "#F39200");
    }
    function disableHoverPopout() {
        //Move out
        $(".fixedfooter").css("border", "2px solid #7D7D7D");
        $(".fixedfooter_img").css('background', 'url("../../../../../Content/theme/default/images/newmenu/popout_menu1.png") no-repeat center');
        $(".fixedfooter_go").css('background', 'url("../../../../../Content/theme/default/images/newmenu/popout_go.png") no-repeat center');
        $(".textPopout").css('color', "black");
    }
    $(".fixedfooter").hover(function () {
        //Move in lẽ
        enableHoverPopout();
    }, function () {
        //Move out chẵn
        if (click % 2 != 0) {
            enableHoverPopout();
        } else {
            disableHoverPopout();
        }

    });
    $("img").hover(function () {
        var arrString = this.dataset.icon.split(".");
        var hoverImageString = arrString[0] + "_Hover." + arrString[1]
        $(this).attr("src", hoverImageString);
    }, function () {
        //$(this).attr("src", "/Content/theme/default/images/icon/quick-icon-interest.png");
        $(this).attr("src", this.dataset.icon);
    });
    $(".fixedfooter_link_item").hover(function () {
        imgIcon = ($(this).find("img"))[0];
        var arrString = imgIcon.dataset.icon.split(".");
        var hoverImageString = arrString[0] + "_Hover." + arrString[1];
        var id = "#" + imgIcon.id;
        $("img" + id).attr("src", hoverImageString);

    }, function () {
        var id = "#" + imgIcon.id;
        $("img" + id).attr("src", imgIcon.dataset.icon)
    });

    // Click Mở tài khoản ngay

    $(".fixedfooter").click(function () {
        $(".fixedfooter_link").animate({
            height: "toggle"
        }, 500, function () {
            // Animation complete.
        });
    })

    var click = 0;
    $(".fixedfooter_go").click(function () {
        //  $(".fixedfooter").unbind('mouseenter mouseleave')
        click = click + 1;
        $(".fixedfooter_link").animate({
            height: "toggle"
        }, 500, function () {
            // Animation complete.
        });
        $("#go").toggleClass("colorchange");
        $(".fixedfooter").css("border", "2px solid #F39200");
        $(".fixedfooter_img").css('background', 'url("../../../../../Content/theme/default/images/newmenu/popout_menu1_change.png") no-repeat center');
        $(".fixedfooter_go").css('background', 'url("../../../../../Content/theme/default/images/newmenu/popout_go_change.png") no-repeat center');
        $(".textPopout").css('color', "#F39200");
        $("#corner1").toggleClass("cornerchange1");
        $("#corner2").toggleClass("cornerchange2");

        var test = document.getElementById("loan_capital");
        var test1 = document.getElementById("open_creadit_card");
        var test2 = document.getElementById("open_book_saving");
        var test3 = document.getElementById("sme_elending");
        var test4 = document.getElementById("open_omni_account");


        if (test == null && test1 != null && test2 != null && test3 != null && test4 != null) {
            $("#open_creadit_card").css({ marginBottom: "20px" });
        }
        else if (test == null && test1 == null && test3 != null && test4 != null) {
            $("#open_book_saving").css({ marginBottom: "20px" });
        }
        else if (test == null && test2 == null && test3 != null && test4 != null) {
            $("#open_creadit_card").css({ marginBottom: "20px" });
        }
        else if (test1 == null && test2 != null && test != null && test3 != null && test4 != null) {
            $("#open_book_saving").css({ marginBottom: "20px" });
        }
        else if (test1 == null && test2 != null && test != null && test3 != null && test4 != null) {
            $("#sme_elending").css({ marginBottom: "20px" });
        }
        else if (test1 == null && test2 != null && test != null && test3 != null && test4 != null) {
            $("#open_omni_account").css({ marginBottom: "20px" });
        }


    });

    $('#mobile_dropdown').click(function (event) {
        event.stopPropagation();
        if ($(window).width() < 800) {
            $(".hotline_dropdown").slideToggle("fast");
        }
    });
    $(".hotline_dropdown").on("click", function (event) {
        event.stopPropagation();
    });


    $(document).on("click", function () {
        $(".hotline_dropdown").hide();
    });

});
